import { useState } from 'react';
import axios from "axios";
import './App.css';

function App() {
  const mapper = [{"name":"","description":""}];
  const [jsonData, setjsonData] = useState(mapper)

  function getData(){
	axios({
		method: "GET",
		url: "/widgets"
	})
	.then(res => {
		setjsonData(res.data)
	}).catch((error) => {
		if(error.response){
			console.log(error.response)
			console.log(error.response.status)
			console.log(error.response.headers)
		}
})}

  function addData(){
	axios({
		method: "GET",
		url: "/addvalues"
	})
  }
  function clearDB(){
	axios({
		method: "GET",
		url: "/cleardb"
	})
  }

  const JsonNamesTable = () =>(
	jsonData.map(item => {(
		<tr>
			<td>{item.name}</td>
			<td>{item.description}</td>
		</tr>
	)
	}))

  return (
    <div className="App">
      <header className="App-header">
	<p>To get your database details: </p><button onClick={getData}>Click me!</button>

	<table>
		<JsonNamesTable></JsonNamesTable>	
	</table>

	<p> To add more values to the details: </p><button onClick={addData}>Add Data</button>
	    <p>To clear the values from the database: </p><button onClick={clearDB}>Clear DB</button>
		</header>
    </div>
  );
}

export default App;
