import { useState } from 'react';
import axios from "axios";
import logo from './logo.svg';
import './App.css';

function App() {

  const [profileData, setProfileData] = useRef([])
  function getData(){
	axios({
		method: "GET",
		url: "/widgets"
	})
	.then((response) => {
		const res = response.data
		setProfileData(({
			profile_name: res,
			about_me: res.about}))
	}).catch((error) => {
		if(error.response){
			console.log(error.response)
			console.log(error.response.status)
			console.log(error.response.headers)
		}
})}
  function addData(){
	axios({
		method: "GET",
		url: "/addvalues"
	})
}

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
	<p>To get your profile details: </p><button onClick={getData}>Click me!</button>
	{profileData && <div>
		<p>Value = {profileData.profile_name}</p>
            </div>
        }
	<button onClick={addData}>AddValue</button>
      </header>
    </div>
  );
}

export default App;
